import { useState, useEffect } from 'react'
import axios from "axios"

function Sidebar({ tagList, getTags }) {

  const [showNewtag, setShowNewtag] = useState(false)
  const [newtagName, setNewtagName] = useState("")

  function Newtag() {
    setShowNewtag(!showNewtag)  
  }

  function Edittag() {

  }

  function handleKeyDown(e) {
    if (e.key === 'Enter') {
      postTag()
    }
  }

  function postTag() {
    axios
      .post("/api/tags", {
        name: newtagName
      })
      .then((res) => {
        setShowNewtag(false)
        getTags()
      })
      .catch((error) => console.log(error))
  }

  const newtagInput = 
    <input 
      className="NewtagInput" 
      onKeyDown={handleKeyDown}
      onChange={(e) => setNewtagName(e.target.value)}
    />

  return (
    <div className='Sidebar'>
      {tagList.map((tag, index) => {
        return <a href="#" key={index}>
          {tag.name}
        </a>
      })}
      <div className="NewEdit">
      <a className="Edittag" onClick={Edittag} href="#">
        {showEdittag ? "Cancel" : "Edit"}
      </a>
      <a className="Newtag" onClick={Newtag} href="#">
        {showNewtag ? "Cancel" : "New"}
      </a>
      {showNewtag ? newtagInput : ""}
      </div>
    </div>
  );
}

export default Sidebar
