import Task from './Task'
import { useState } from 'react'

function Tasks({ filteredTaskList, tagList, getUserTags, refreshTask, refreshDeletedTask }) {
  function reorder(xs) {
    return (
      filteredTaskList
        .filter((task) => {return task.status !== 'done'})
        .concat(filteredTaskList.filter((task) => {return task.status === 'done'}))
    )
  }

  return (
    reorder(filteredTaskList)
      .map((task, index) => {
        <Task 
              key={task.id} 
              index={index} 
              task={task} 
              tagList={tagList} 
              getUserTags={getUserTags}  
              refreshTaskNew={refreshTask}
              refreshDeletedTaskNew={refreshDeletedTask} />
      })
  )

  return (
    <div className="Tasks">
      {filteredTaskList.map((task, index) => {
        if (task.status !== "done") {
          return (
            <Task 
              key={task.id} 
              index={index} 
              task={task} 
              tagList={tagList} 
              getUserTags={getUserTags}  
              refreshTaskNew={refreshTask}
              refreshDeletedTaskNew={refreshDeletedTask} />)
        }
      })}
      {filteredTaskList.map((task, index) => {
        if (task.status === "done") {
          return (
            <Task 
              key={task.id} 
              index={index} 
              task={task} 
              tagList={tagList} 
              getUserTags={getUserTags}  
              refreshTaskNew={refreshTask}
              refreshDeletedTaskNew={refreshDeletedTask} />)
        }
    })}
    </div>
  );
}

export default Tasks
