      className='newUserInput' 
      onChange={(e) => {setNewUsername(e.target.value)}}
      onKeyDown={handleKeyDown}>
    </input>

  function postUser() {
    axios
      .post("/api/users", {
        username: newUsername
      })
      .then((res) => {
      })
      .catch((error) => console.log(error))
  }

