    if (e.key === 'Enter') {
      if (newUsername.length > 20) {
        alert('Please use names with a maximum of 20 characters')
      } else {
        postUser()
      }
    }
  }

  return (
    <div className='Users'>
      <h3>Select user:</h3>
      <div className='UserList'>
        {
          userList.map((user, index) => {
            return (
              <li key={index} onClick={() => setCurrentUser(user)}>
                {user.username}
              </li>)})
