        placeholder='Search tasks'/>
      <select>
        <option>Deadline</option>
        <option>Created at</option>
        <option>Alphabetical</option>
      </select>
      <button onClick={onClickNewTask}>
        {buttonState ? 'Close' : 'New Task'}    
      </button>
    </div>
  );
}

export default Topbar
