function TagTask({ task, tagList, refreshTask }) {
  function addTaskToTag(id) {
    axios
      .put(`/api/tasks/${id}`, {
        do: "add",
        task: id
      })
      .then((res) => {
        refreshTask()
      })
      .catch((error) => console.log(error))
  }

  return (<div className="TagTaskList">
    {tagList.map((tag, index) => {
      return <h3 key={index}>{tag.name}</h3>
    })}
  </div>)
}

export default TagTask
