import Task from './Task'
import { useState, useEffect } from 'react'

function Tasks({ filteredTaskList, tagList, getUserTags, refreshTask, refreshDeletedTask }) {
  const [renderTaskList, setRenderTaskList] = useState([])

  useEffect(() => {
    setRenderTaskList([])
  }, [filteredTaskList])

  return (
    <div className="Tasks">
      {filteredTaskList.map((task, index) => {
        const taskComponent = 
          <Task 
                key={task.id} 
                index={index} 
                task={task} 
                tagList={tagList} 
                getUserTags={getUserTags}  
                refreshTaskNew={refreshTask}
                refreshDeletedTaskNew={refreshDeletedTask} />
        if (task.status !== "done") {
          return taskComponent
        } else {
          renderTaskList.push(taskComponent)
        }
    })}
    </div>
  );
}

export default Tasks
