import Task from './Task'
import { useState, useEffect } from 'react'

function Tasks({ filteredTaskList, tagList, getUserTags, refreshTask, refreshDeletedTask }) {
  const [renderTaskList, setRenderTaskList] = useState([])

  useEffect(() => {
    setRenderTaskList([])
  }, [filteredTaskList])

  return (
    <div className="Tasks">
      {filteredTaskList
        .filter((task) => {return task.status !== "done"}) 
        .map(task => {
          return (
            <Task 
              key={task.id} 
              task={task} 
              tagList={tagList} 
              getUserTags={getUserTags}  
              refreshTaskNew={refreshTask}
              refreshDeletedTaskNew={refreshDeletedTask} />)
        })
      }
    </div>
  );
}

export default Tasks
